INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (100, 'মুহম্মদ জাফর ইকবাল', 'বাংলাদেশের কিশোর-কিশোরী পাঠকদের কাছে সবচেয়ে জনপ্রিয় নাম মুহম্মদ জাফর ইকবাল। তিনি মূলত এ দেশের একজন বিখ্যাত লেখক, পদার্থবিদ এবং শিক্ষাবিদ। কিশোর সাহিত্য, শিশুতোষ গ্রন্থ, বৈজ্ঞানিক কল্পকাহিনী, গণিত বিষয়ক বই এর জন্য খুব অল্প সময়েই জনপ্রিয়তা লাভ করেন তিনি।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (101, 'হুমায়ূন আহমেদ', 'বাংলা সাহিত্যের এক কিংবদন্তী হুমায়ূন আহমেদ। বিংশ শতাব্দীর বাঙালি লেখকদের মধ্যে তিনি অন্যতম স্থান দখল করে আছেন। একাধারে ঔপন্যাসিক, ছোটগল্পকার ও নাট্যকার এ মানুষটিকে বলা হয় বাংলা সায়েন্স ফিকশনের পথিকৃৎ। নাটক ও চলচ্চিত্র পরিচালক হিসেবেও তিনি বেশ সমাদৃত। বাদ যায়নি গীতিকার কিংবা চিত্রশিল্পীর পরিচয়ও। ', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (102, 'সমরেশ মজুমদার', '১৯৪২ সালের ১০ই মার্চ পশ্চিমবঙ্গের জলপাইগুড়িতে জন্ম বিশিষ্ট কথাসাহিত্যিক এবং ঔপন্যাসিক সমরেশ মজুমদারের। তাঁর শৈশব কাটে প্রকৃতির কোলে, চা বাগানে ঘুরে, আদিবাসী শিশুদের সাথে খেলে। এ কারণেই সমরেশ মজুমদার এর বই সমগ্রতে বারবার উঠে আসে পশ্চিমবঙ্গের জলপাইগুড়ি, চা বাগান, বৃষ্টি কিংবা পাহাড়ের কথা।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (103, 'রবীন্দ্রনাথ ঠাকুর', 'বিশ্বকবি রবীন্দ্রনাথ ঠাকুর ছিলেন একাধারে ঔপন্যাসিক, নাট্যকার, প্রাবন্ধিক, ছোটগল্পকার, চিত্রশিল্পী, সংগীতস্রষ্টা, অভিনেতা, কন্ঠশিল্পী, কবি, সমাজ-সংস্কারক এবং দার্শনিক। গীতাঞ্জলি কাব্যগ্রন্থের ইংরেজি অনুবাদের জন্য প্রথম বাঙালি হিসেবে ১৯১৩ সালে তিনি সাহিত্যে নোবেল পুরষ্কার লাভ করেন।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (104, 'সুনীল গঙ্গোপাধ্যায়', 'বিশ শতকের শেষাংশে জন্ম নেওয়া সব্যসাচী একজন বাঙ্গালি সাহিত্যিক সুনীল গঙ্গোপাধ্যায়। একাধারে কবি, ঔপন্যাসিক, ছোটগল্পকার, সম্পাদক, সাংবাদিক ও কলামিস্ট- এমন বহু পরিচয়ে সাহিত্যের অগণিত ক্ষেত্রে তিনি রেখেছেন তাঁর সুকুমার ছাপ। ', '/images/writer/defaultbook.jpg');
