INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (100, 'মুহম্মদ জাফর ইকবাল', 'বাংলাদেশের কিশোর-কিশোরী পাঠকদের কাছে সবচেয়ে জনপ্রিয় নাম মুহম্মদ জাফর ইকবাল। তিনি মূলত এ দেশের একজন বিখ্যাত লেখক, পদার্থবিদ এবং শিক্ষাবিদ। কিশোর সাহিত্য, শিশুতোষ গ্রন্থ, বৈজ্ঞানিক কল্পকাহিনী, গণিত বিষয়ক বই এর জন্য খুব অল্প সময়েই জনপ্রিয়তা লাভ করেন তিনি।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (101, 'হুমায়ূন আহমেদ', 'বাংলা সাহিত্যের এক কিংবদন্তী হুমায়ূন আহমেদ। বিংশ শতাব্দীর বাঙালি লেখকদের মধ্যে তিনি অন্যতম স্থান দখল করে আছেন। একাধারে ঔপন্যাসিক, ছোটগল্পকার ও নাট্যকার এ মানুষটিকে বলা হয় বাংলা সায়েন্স ফিকশনের পথিকৃৎ। নাটক ও চলচ্চিত্র পরিচালক হিসেবেও তিনি বেশ সমাদৃত। বাদ যায়নি গীতিকার কিংবা চিত্রশিল্পীর পরিচয়ও। ', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (102, 'সমরেশ মজুমদার', '১৯৪২ সালের ১০ই মার্চ পশ্চিমবঙ্গের জলপাইগুড়িতে জন্ম বিশিষ্ট কথাসাহিত্যিক এবং ঔপন্যাসিক সমরেশ মজুমদারের। তাঁর শৈশব কাটে প্রকৃতির কোলে, চা বাগানে ঘুরে, আদিবাসী শিশুদের সাথে খেলে। এ কারণেই সমরেশ মজুমদার এর বই সমগ্রতে বারবার উঠে আসে পশ্চিমবঙ্গের জলপাইগুড়ি, চা বাগান, বৃষ্টি কিংবা পাহাড়ের কথা।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (103, 'রবীন্দ্রনাথ ঠাকুর', 'বিশ্বকবি রবীন্দ্রনাথ ঠাকুর ছিলেন একাধারে ঔপন্যাসিক, নাট্যকার, প্রাবন্ধিক, ছোটগল্পকার, চিত্রশিল্পী, সংগীতস্রষ্টা, অভিনেতা, কন্ঠশিল্পী, কবি, সমাজ-সংস্কারক এবং দার্শনিক। গীতাঞ্জলি কাব্যগ্রন্থের ইংরেজি অনুবাদের জন্য প্রথম বাঙালি হিসেবে ১৯১৩ সালে তিনি সাহিত্যে নোবেল পুরষ্কার লাভ করেন।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (104, 'সুনীল গঙ্গোপাধ্যায়', 'বিশ শতকের শেষাংশে জন্ম নেওয়া সব্যসাচী একজন বাঙ্গালি সাহিত্যিক সুনীল গঙ্গোপাধ্যায়। একাধারে কবি, ঔপন্যাসিক, ছোটগল্পকার, সম্পাদক, সাংবাদিক ও কলামিস্ট- এমন বহু পরিচয়ে সাহিত্যের অগণিত ক্ষেত্রে তিনি রেখেছেন তাঁর সুকুমার ছাপ। ', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (105, 'হাসান আজিজুল হক', 'সাম্প্রতিক বাংলা সাহিত্যের গৌরবময় একটি নাম হাসান আজিজুল হক। ১৯৭৩ সাল থেকে রাজশাহী বিশ্ববিদ্যালয়ে দর্শনের অধ্যাপক, এখন অবসরপ্রাপ্ত। অধ্যাপনার সঙ্গে সঙ্গে দীর্ঘকাল অনেক গল্পের স্ৰষ্টা তিনি।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (106, 'আখতারুজ্জামান ইলিয়াস', 'আখতারুজ্জামান ইলিয়াস রচনাসমগ্র এবং বাংলা সাহিত্যে বহুমাত্রিক অবদানের জন্য ১৯৮৩ সালে বাংলা একাডেমি পুরস্কার এবং ১৯৯৬ সালে আনন্দ পুরস্কার পান।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (107, 'সেলিনা হোসেন', '২১টি উপন্যাস, ৭টি গল্পগ্রন্থ ও ৪টি প্রবন্ধগ্রন্থের রচয়িতা সেলিনা হোসেন বাংলাদেশের একজন জনপ্রিয় কথাসাহিত্যিক। সমকালীন রাজনৈতিক সংকট ও দ্বন্দ্বের উৎস ও প্রেক্ষাপট উঠে এসেছে সেলিনা হোসেন এর বই সমূহ-তে। সেলিনা হোসেন এর বই সমগ্র অনূদিত হয়েছে ইংরেজি, রুশসহ একাধিক ভাষায়। ', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (108, 'সত্যজিৎ রায়', 'সত্যজিৎ রায় এক বাঙালি কিংবদন্তী, যিনি বিংশ শতাব্দীর অন্যতম শ্রেষ্ঠ চলচ্চিত্রকারের খ্যাতি অর্জন করেছিলেন বিশ্বদরবারে। কর্মজীবনে একইসাথে চিত্রনাট্য রচনা, সঙ্গীত স্বরলিপি রচনা, সম্পাদনা, প্রকাশক, চিত্রকর, গ্রাফিক নকশাবিদ, লেখক ও চলচ্চিত্র সমালোচক হিসেবে ভূমিকা রেখেছেন অসম্ভব গুণী এই মানুষটি।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (109, 'বিভূতিভূষণ বন্দ্যোপাধ্যায়', 'বাংলা সাহিত্যের অন্যতম পথিকৃৎ বিভূতিভূষণ বন্দ্যোপাধ্যায় বেশ কিছু কালজয়ী উপন্যাস রচনার মাধ্যমে জয় করে নিয়েছেন বাংলা ভাষাভাষী পাঠকদের হৃদয়। শুধু উপন্যাসই নয়, এর পাশাপাশি তিনি রচনা করেছেন বিভিন্ন ছোটগল্প, ভ্রমণকাহিনী, দিনলিপি ইত্যাদি।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (110, 'মানিক বন্দ্যোপাধ্যায়', 'শ্রমজীবী মানুষের সংগ্রাম, মধ্যবিত্ত সমাজের কৃত্রিমতা, নিয়তিবাদ ইত্যাদি বিষয়কে লেখার মধ্যে তুলে এনে বাংলা সাহিত্যে যিনি অমর হয়েছেন, তিনি হলেন প্রখ্যাত ভারতীয় বাঙালি কথাসাহিত্যিক মানিক বন্দ্যোপাধ্যায়। ', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (111, 'শীর্ষেন্দু মুখোপাধ্যায়', 'পশ্চিমবঙ্গের জনপ্রিয় কথাসাহিত্যিক শীর্ষেন্দু মুখোপাধ্যায় ১৯৩৫ সালের ২রা নভেম্বর বাংলাদেশের ময়মনসিংহ জেলায় জন্মগ্রহণ করেন। ১৯৮৮ সালে ‘মানবজমিন’ উপন্যাসের জন্য অর্জন করেন ‘সাহিত্য আকাদেমি পুরস্কার’। ', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (112, 'আনিসুল হক', 'আনিসুল হক, বাংলাদেশে গত শতাব্দীর আশির দশকে আবির্ভূত হওয়া একজন প্রখ্যাত কবি, কথাসাহিত্যিক, নাট্যকার ও সাংবাদিক। কবিতা, গল্প, উপন্যাস, গদ্যকার্টুন, রম্যরচনা, ভ্রমণকাহিনী, শিশুসাহিত্যসহ সাহিত্যের নানা ক্ষেত্রে রয়েছে তার সাবলীল বিচরণ।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (113, 'আহমদ ছফা', 'বাঙালি মুসলিম লেখকদের মধ্যে অন্যতম কীর্তিমান কথাসাহিত্যিক আহমদ ছফা একাধারে ছিলেন কবি, ঔপন্যাসিক, সাংবাদিক, গণবুদ্ধিজীবী ও চিন্তাবিদ।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (114, 'তামিম শাহরিয়ার সুবিন', 'তামিম শাহরিয়ার সুবিন পেশায় একজন সফটওয়্যার ইঞ্জিনিয়ার। তিনি বাংলাদেশে থাকাকালে মুক্ত সফটওয়্যার লিমিটেড ও দ্বিমিক কম্পিউটিং নামক দুটি প্রতিষ্ঠান গড়ে তোলেন। বাংলাদেশ গণিত অলিম্পিয়াডের একজন একাডেমিক কাউন্সিলর হিসেবেও নিয়োজিত রয়েছেন।', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (115, 'জহির রায়হান', 'বাংলা সাহিত্য ও চলচ্চিত্রের এক উজ্জ্বল নক্ষত্র জহির রায়হান, যিনি শুধু একজন প্রখ্যাত সাহিত্যিকই নন, একাধারে ছিলেন গল্পকার, ঔপন্যাসিক, সাংবাদিক ও চলচ্চিত্র পরিচালক। ', '/images/writer/defaultbook.jpg');
INSERT INTO ROKOMARI.Writer(ID, Name, Description, Image) VALUES (116, 'সৈয়দ মুজতবা আলী', 'বিশিষ্ট পণ্ডিত ও রম্যরচয়িতা সৈয়দ মুজতবা আলী বাংলাদেশের একজন খ্যাতিমান সাহিত্যিক। শুধু রম্যরচনাই নয়, ছোটগল্প, উপন্যাস, অনুবাদ, ভ্রমণকাহিনী সাহিত্যের ইত্যাদি বিশেষ শাখায় রচিত সৈয়দ মুজতবা আলী এর বই সমূহ অর্জন করেছে বিশেষ খ্যাতি। ', '/images/writer/defaultbook.jpg');
